class Solution {
public:
    int maxOperations(string s) {

        int n=s.size();

        int ans=0;
        int cnt=0;

        for(int i=0;i<n-1;i++){
            if(s[i]=='1')
                cnt++;
            if(s[i]=='1' && s[i+1]=='0')ans+=cnt;
            cout<<ans<<" ";
        }
        cout<<'\n';
        return ans;

    }
};
