class Solution {
public:
    int maxOperations(string s) {
        stack<int>st;
        int ans=0; bool flag=false;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){st.push('1'); flag= true;}
            else if(s[i]=='0' && flag==true){ans+=st.size(); flag=false;}
        }
        return ans;
    }
};
