class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int res = 0;
        boolean flag = false;
        for (int i = 0; i < s.length(); i++) {
            if(s.charAt(i) == '1'){
                count++;
                flag= true;
            }
            if(flag && s.charAt(i) == '0'){
                res += count;
                flag = false;
            }
        }
        return res;
    }
}
