class Solution {
public:
    int maxOperations(string s) {
        int result = 0, count_ones = 0;
        int i = 0;
        while(i < s.length()){
            if(s[i] == '1') count_ones++;
            if(s[i] == '1' && s[i + 1] == '0') {
                result += count_ones;
            }

            i++;
        }

        return result;
    }
};
