class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int cnt=0;
        int prev=0;

        for (int z:s){
            if(z=='1'){
                cnt++;
                prev=1;
            }
            else{
                if(prev!=0){
                    ans+=cnt;
                }
                prev=0;
            }

        }
        return ans;


    }
};
