class Solution {
    public int maxOperations(String s) {
        char[] ch = s.toCharArray();
        int n = ch.length, ones = 0, res = 0;
        for(int i = 0; i < n; i++) {
            if(i > 0 && ((ch[i] == '1' && ch[i - 1] == '0') || (ch[i] == '0' && i == n - 1))) {
                res += ones;
            }
            if(ch[i] == '1') ones++;
        }
        return res;
    }
}
