class Solution {
public:
    int maxOperations(string s) {
        int ans=0,n=s.length(),cnt=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1')cnt++;
            else if(s[i]=='0'){
                ans+=cnt;
                while(i < n && s[i] == '0') i++;
                i--;
            }
        }
        return ans;
    }
};
