class Solution:
    def maxOperations(self, s: str) -> int:
        index = 0
        res = 0
        count = 0
        while index < len(s) - 1:
            if s[index] == "1":
                count += 1
                if s[index + 1] == "0":
                    res += count
            index += 1
        return res
