class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> arr;
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') continue;
            int j = i;
            while (j < n && s[j] == s[i]) j++;
            arr.push_back(j - i);
            i = j - 1;
        }
        if (s.back() == '0') {
            arr.push_back(0);
        }
        int ans = 0;
        for (int i = 1; i < arr.size(); i++) {
            ans += arr[i - 1];
            arr[i] += arr[i - 1];
        }
        return ans;
    }
};
