class Solution {
    public int maxOperations(String s) {
        List<Integer> list = new ArrayList<>();

        int result = 0;
        int count = 0;

        boolean flag = false;

        for(int i = 0; i < s.length(); i++) {
            if(s.charAt(i) == '1') {
                list.add(1);
                flag = true;
            }
            if(s.charAt(i) == '0' && flag) {
                list.add(0);
                flag = false;
            }
        }

        // Compute result
        for(int i : list) {
            if(i == 1) {
                count++;
            } 
            else {
                result += count;
            }
        }
        return result;
    }
}
