class Solution {
    public int maxOperations(String s) {
        char[] ch=s.toCharArray();
        int count=0,frd=0,one=0;
        for(int i=0;i<s.length()-1;i++){
            if(ch[i]=='1'&&ch[i+1]=='0'){                
                count+=one+frd+1;
                frd=one+frd+1;
                one=0;
            }
            else if(ch[i]=='1'){
                one++;
            }
        }
        return count;
    }
}
