class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, ans = 0;
        for(int i = 0 ; i < s.size() ; i++ )
        {
             if(s[i] == '1')
             {
                if(i!=0 && s[i-1] == '0')
                {
                   ans += cnt;
                   cnt++;
                }
                 else
                 {
                    cnt++;
                 }
             }
        }
        int n = s.size();
        if(s[n-1] != '1') ans+=cnt;
        return ans;
    }
};
