class Solution {
    public int maxOperations(String s) {
        int res=0;
        int count=0;
        boolean meetZero=false;
        for(char c:s.toCharArray()){
            if(c=='0'&&!meetZero){
                res+=count;
                meetZero=true;
            }else if(c=='1'){
                count++;
                meetZero=false;
            }
        }
        return res;
    }
}
