/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
    let res=  0;

    let c = 1;
    let i = 0;
    while (i < s.length) {
        if (s[i] === '0') i++;
        else {
            if (i + 1 === s.length) return res;
            if (s[i+1] === '0') {
                res += c;
            }
            c++;
            i++;
        }
    }
    return res;
};
