class Solution:
    def maxOperations(self, s: str) -> int:
        s = s.rstrip('1').lstrip('0')
        if not s: return 0
        A = []
        for i, g in groupby(s, key=lambda x: x == '1'):
            if i:
                g = len(list(g))
                A.append(g)
        n = len(A)
        return sum(a * (n - i) for i, a in enumerate(A))
