class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, op = 0, n = s.length();
        for(int i = 0; i < n-1; i++){
            if(s[i] == '1')cnt++;
            if(s[i] == '1' && s[i+1] == '0'){
                op += cnt;
            }
        }
        return op;
    }
};
