class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        stack<char> st;
        int i = 0;
        int n = s.length();
        int one = 0;
        while( i < n ){
            if( s[i] == '1' ){
                // while(one){
                //     st.push('1');
                //     one--;
                // }
                st.push(s[i]);
                i++;
            }else if( s[i] == '0' ){

                //one = 0;
                int newc = 0;
                while( !st.empty() && st.top() == '1' ){
                    st.pop();
                    //ans++;
                    newc++;
                }
                if(newc){
                    one += newc;
                    ans += one;
                }

                st.push(s[i]);
                i++;
            }
        }

        return ans;
    }
};
