class Solution {
public:
    int maxOperations(string s) {
        int result = 0;
    int consecutiveOnes = 0;
    bool isConsecutive = true;

    for (int i = 0; i < s.size(); i++) {
        if (s[i] == '1') {
            consecutiveOnes++;
            isConsecutive = true;
        } else {
            if (isConsecutive) {
                result += consecutiveOnes;
            }
            isConsecutive = false;
        }
    }

    return result;
    }
};
