class Solution {
public:
    int maxOperations(string s) {
        vector<int> cnt1;
        int t = 0;
        for (auto &c:s) {
            if (c == '1')   t++;
            else {
                if (t)  {
                    cnt1.push_back(t);
                    t = 0;
                }
            }
        }
        if (t)  cnt1.push_back(t);
        // for (auto it:cnt1)  cout << it << " ";
        // cout << endl; 

        int ans = 0;
        t = 0;
        if (cnt1.size()) {
            for (int i = 0; i < cnt1.size() - 1; i++) {
                cnt1[i + 1] += cnt1[i];
                ans += cnt1[i];
            }
            bool end0 = (s.back() == '0') ? true:false;
            if (end0)   ans += cnt1.back();
        }
        return ans;
    }
};
