class Solution {
public:
    int maxOperations(string s){
        int n=s.size();

        bool flag=false;
        string str="";
        int i=0;
        while(i<n){
            if(s[i] == '0' && flag==true){
                str += '0';
                flag=false;
            }
            else if(s[i] == '1'){
                str += '1';
                flag=true;
            }
            i++;
        }

        int res=0;
        int c=0;
        for(int i=0; i<str.size(); i++){
            if(str[i] == '1'){
                c += 1;
            }
            else{
                res += c;
            }
        }

        return res;
    }
};
