class Solution {
public:
    int maxOperations(string s) {
        vector<int>arr ;
        int c  = 0 ;
        for (int i = 0 ; i<s.length() ;  i++){
            if(s[i] == '1'){
                c++;
            }
            else{
                if(c){
                    arr.push_back(c) ;
                    c =0 ;
                }
            }
        }
        int sum = 0 ;
        int ans = 0 ;
        for (int i = 0 ; i<arr.size() ;  i++){
            sum += arr[i] ;
            ans += sum ;

        }
        return  ans ;
    }
};
