class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int cnt=0,ans=0;
        for(int i=0;i<n-1;i++)
        {
            if(s[i]=='1'&&s[i+1]=='0')
            {
                cnt++;
                ans+=cnt;
            }
            else if(s[i]=='1') cnt++;

        }
        return ans;

    }
};
