class Solution {
public:
    int maxOperations(string s) {
        int ans  = 0, n = s.size();
        int count = 0;
        for(int i = 0; i < n; i++){
            if(s[i] == '1'){
                count++;
            } else {
                if(count && s[i - 1] != s[i])   ans += count;
            }
        }
        return ans;
    }
};
