class Solution {
    public int maxOperations(String s) {
        int ones = 0;
        int opr = 0;
        boolean zeros = false;
        for(int i = 0; i<s.length(); i++) {
            if(s.charAt(i) == '1') {
                if(zeros) {
                    opr += ones;
                    zeros = false;
                }
                ones++;
            } else {
                zeros = true;
            }
        }
        if(zeros) opr += ones;
        return opr;
    }
}
