class Solution {
    func maxOperations(_ s: String) -> Int {

        let n = s.count
        var num = 0
        var ans = 0
        for i in s.indices{
            if i != s.indices.last {
                if s[i] == "1"{
                    num += 1
                    if s[s.index(after: i)] == "0"{
                        ans += num
                    }
                }

            }

        }

        return ans

    }
}
