class Solution {
public:
    int maxOperations(string s) {

        int ans=0, cnt=0;
        bool flag=1;

        for(int i=0; i<s.size(); i++)
        {
            if(s[i]=='1')
            {
                cnt++;
                flag=1;
            }
            else
            {
                if(flag==1)
                {
                    ans+=cnt;
                }

                flag=0;
            }
        }

        return ans;
    }
};
