class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int res=0;
        int last=0;
        for (int i=n-2; i>=0; i--) {
            if (s[i]=='1') {
                if (s[i+1]=='0') {
                    last++;
                    res += last;
                } else {
                    res += last;
                }
            }
        }
        return res;
    }
};
