class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int p = 0;
        bool has_one = false;
        std::vector<int> bits;

        // Traverse the string and build the bits vector
        for (char ch : s) {
            if (ch == '1') {
                bits.push_back(1);
                has_one = true;
            } else if (ch == '0' && has_one) {
                bits.push_back(0);
                has_one = false;
            }
        }

        // Calculate the result based on the bits vector
        for (int bit : bits) {
            if (bit == 1) {
                p += 1;
            } else {
                ans += p;
            }
        }

        return ans;
    }
};
