class Solution { 
public: 
    int maxOperations(string s) { 
        int res=0;
        int p=0;
        int r=0; 
        int f=0; int n=s.size();
        vector<int> v; 
        for(int i=0;i<n;i++){ 
            if(s[i]=='1'){ 
                v.push_back(1); 
                f=1; 
            } 
            if(s[i]=='0' and f==1){ 
                v.push_back(0); 
                f=0; 
            } 
        } 
        for(auto i:v){ 
            if(i==1) p++; 
            else  res+=p; 
        } 
        return res; 
    } 
};
