class Solution {
public:
    int maxOperations(string s) {
  int n = s.size();

  int moves = 0;
  int groupSize = 0;

  for (int i = 0; i < n; i++) {
    if (s[i] == '1') {
      groupSize++;
    } else if (i == n - 1 || s[i + 1] == '1') {
      moves += groupSize;
    }
  }

  return moves;
}

};
