class Solution {
public:
    int maxOperations(string s) {
        int zeroBlocks=0;
        int ans=0;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='1'){
                if(i+1!=s.size() && s[i+1]=='0'){    
                    zeroBlocks++;
                }
                ans+=zeroBlocks;
            }
        }
        return ans;
    }
};
