class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int onecnt = 0;
        char prev = '0';
        for (char c : s.toCharArray()) {
            if (c == '0' && prev == '1') {
                ans += onecnt;
            }
            if (c == '1') {
                onecnt ++;
            }
            prev = c;
        }
        return ans;
    }
}
