class Solution 
{
public:
    int maxOperations(string s) 
    {
        int ones = 0, ans = 0;
        for(int i=0;i<s.length()-1;)
        {
            while(s[i] == '1')
            {
                ones++;
                i++;
            }
            if(i < s.length())  ans += ones;
            while(i < s.length() && s[i] == '0')
            {
                i++;
            }
        }
        return ans;
    }
};
