class Solution {
public:
    int maxOperations(string sx) {

        int resxult=0;
        int count=0;
        bool foundOne=false;
        vector<int> bitSequence ;

        for(char ch:sx) {
            if(ch=='1') {
                bitSequence.push_back(1);
                foundOne=true;
            }
            else if(ch=='0' && foundOne) {
                bitSequence.push_back(0);
                foundOne=false; 
            }
        }

        for(int bit:bitSequence) {
            if(bit==1) {
                count++;
            }
            else
                resxult+=count;
        }


        return resxult;

    }
};
