class Solution {
public:
    int maxOperations(string s) {
        int N = s.size();
        int cnt = 0, ans = 0;
        bool flag = false;
        for (char ch : s) {
            if (ch == '1') cnt++, flag = true;
            if (ch == '0' && flag) ans += cnt, flag = false;
        }
        return ans;
    }
};
