class Solution {
    public int maxOperations(String s) {
        int answer=0;
        int currOnes=0;
        boolean flag=false;
        ArrayList<Character> arrList=new ArrayList<>();
        for(int i=0;i<s.length();i++){
            if(s.charAt(i)=='1'){
                arrList.add('1');
                flag=true;
            }
            if(s.charAt(i)=='0'&&flag==true){
                arrList.add('0');
                flag=false;
            }
        }
        for(int i=0;i<arrList.size();i++){
            if(arrList.get(i)=='1') currOnes++;
            else answer+=currOnes;
        }
        return answer;
    }
}
