class Solution {
public:
    int solve(string s, int n) 
    {
        int one = 0;
        bool flag = false;
        int result = 0;

        for (int i = 0; i < n; i++) 
        {
            char ch = s[i];
            if (ch == '1') 
            {
                one++;
                flag = true;
            } 
            else if (ch == '0' && flag) 
            {
                flag = false;
                result = result + one;
            }
        }
        return result;
    }

    int maxOperations(string s)
    {
        int n = s.size();

        int result=solve(s,n);

        return result;
    }
};
