class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        int res=0;
        int cnt=0, flag=1;
        for(int i=0; i<n; i++)
        {
            if(s[i]=='1')
            {
                cnt++;
                flag=1;
            }
            else
            {
                if(flag)
                res+=cnt;
                flag=0;
            }
        }
        return res;
    }
};
