class Solution {
public:
    int maxOperations(string s) {
            int n = s.length();
    int maxi=0;

            int fl=0;
            vector<int> v;
            for (int i=0;i<s.length();i+=1){
                    if (s[i]=='1'){
                        v.push_back(1);
                        fl=1;
                    }
                    if (s[i]=='0'&&fl){
                            v.push_back(0);
                            fl=0;
                }
            }
            int res=0;
            for (int i=0;i<v.size();i+=1){
                    if (v[i]==1){
                            res+=1;
                }
                    else {
                            maxi+=res;
                }
        }
            return maxi;

    }
};
