class Solution {
public:
    int maxOperations(string s) {
      int c1 = 0;
      int ans = 0;

      for(int i = 0; i < s.size()-1 ; i++)
      {
            if(s[i]=='1' && s[i+1]=='0')
            {
                c1++;
                ans+=c1;
            }
            else if(s[i]=='1' && s[i+1]=='1'){
                c1++;
            }
      }  

      return ans;
    }
};
