class Solution {
public:
    int maxOperations(string s) {
        // 1010101010
        int cnt = 0, ans = 0, n = s.length(), i = 0;
        while (i < n) {
            if (s[i] == '1') {
                cnt++;
                i++;
            } else {
                ans += cnt;
                while (i < n && s[i] == '0') {
                    i++;
                }
            }
        }
        return ans;
    }
};
