class Solution {
public:
int maxOperations(string s) {
    vector<int> a;
    int streak = 0, ans = 0, sum = 0;
    for (char c : s) {
        if (c == '1')
            ++streak;
        else {
            if (streak)
                a.push_back(streak);
            streak = 0;
        }
    }

    for (int b : a) {
        sum += b;
        ans += sum;
    }

    return ans;
}
};
