class Solution {
public:
    int maxOperations(string s) {
        int oneOnLeft = 0,ans = 0;
        for (int i=0;i<s.length();i++) {
            if (s[i] == '1') oneOnLeft++;
            else if (s[i] == '0') {
                if (i == 0 || s[i-1] != '0') ans += oneOnLeft;
            }
        }
        return ans;
    }
};
