class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        vector<int> prefArr(n);
        prefArr[0] = (s[0] == '1' ? 1 : 0);

        for(int i = 1; i < n; i++) 
            prefArr[i] = prefArr[i-1] + (s[i] == '1' ? 1 : 0);


        int ans = 0;
        bool flag = false;

        for(int i = n-1; i >= 0; i--) {
            if(s[i] == '0') {
                if(flag) continue;
                ans += prefArr[i];
                flag = true;
            }

            else 
                flag = false;
        }


        return ans;
    }
};
