class Solution {
public:
    int maxOperations(string s) {
        int onecnt=0;
        int ans = 0 ;
        int n= s.length();
        for (int i=0 ; i<s.length() ; i++)
        {
            // cout << i << " -> " << onecnt << "->" << ans << endl;
            if (s[i]=='1') onecnt++;
            else if (s[i]=='0') 
            {
                ans+=onecnt;
                while (i<n && s[i]=='0') i++;
                i--;
            }

        }
        return ans;
    }
};
