class Solution {
    fun maxOperations(s: String): Int {
        var ms = 0
        s.forEach {
            if (it == '1') {
                ms++
            }
        }
        var ans = 0L
        var ji1 = 0L
        for (i in 0 until s.length) {
            if (i + 1 < s.length && s[i] == '1' && s[i + 1] == '0' && ms >= 1) {
                ji1++
                ms--
                ans += ji1
            } else if (s[i] == '1') {
                ji1++
                ms--
            }
        }
        return ans.toInt()
    }
}
