class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        vector<int> zero(n+1,0);
        int zo = 0;
        for(int i = n-1; i >= 0; i--){
            if(s[i] == '0'){
                zo = 1;
                zero[i] = zero[i+1];
            }
            else{
                zero[i] = zero[i+1] + zo;
                zo = 0;
                ans += zero[i];
            }
        }
        return ans;
    }
};
