class Solution {
public:
    int maxOperations(string s) {
        // reverse(s.begin(),s.end());
        int ans = 0;
        int countZero = 0;  
        int countOnes = 0;  

        int f=0;
        for (int i=0;i<s.size();i++) {
            char c=s[i];
            if (c == '0') {
                if(f==1)ans+=countOnes;
                while(i<s.size()-1 && s[i+1]=='0'){
                    i++;
                    continue;
                }

            } else if (c == '1') {
                 f=1;
                countOnes++;
            }
        }

        return ans;
    }
};
