class Solution {
public:
  int maxOperations(string s) {
    int ret = 0, cnt1 = 0;

    for (int i = 0; i < s.size(); i++) {
      if (s[i] == '0' && (i == 0 || s[i-1] == '1')) {
        ret += cnt1;
      }
      if (s[i] == '1') {
        cnt1++;
      }

    }
    return ret;
  }
};
