class Solution {
    public int maxOperations(String s) {
        int operations = 0;
        int len = s.length();
        int continuousOneCnt = 0;

        for(int index=0; index < len; index++){
            if(s.charAt(index) == '0'){
                operations += continuousOneCnt;
                while(index < len && s.charAt(index) == '0') { index++;}
                index--;
            } else{
                continuousOneCnt++;
            }
        }

        return operations;
    }
}
